import requests
import base64
import json

# 1. 准备参数（请替换为你的实际值）
url = "https://ocr.fmppt.com/api/v3/accurate"  # 假设 {proxy} 是 "cn."
user_type = "1"
union_id = "2e401d7d6f09272212e8b187a2ca8726"
action_id = "7c498ad8-55ed-4c6f-9a04-22971e4f23d5"

img_base64 = ""

# 2. 构建请求体
payload = {
    "usertype": user_type,
    "unionid": union_id,
    "actionid": action_id,
    "image": img_base64
}

# 3. 设置请求头（通常需要指定 Content-Type）
headers = {
    'Content-Type': 'application/json'
}

# 4. 发送 POST 请求
response = requests.post(url, headers=headers, data=json.dumps(payload))

# 5. 解析响应
if response.status_code == 200:
    result = response.json()
    print("识别成功:", result)
else:
    print("请求失败，状态码:", response.status_code)
    print("错误信息:", response.text)